define({"topics" : [{"title":"Request endpoint","shortdesc":"\n                     <p class=\"shortdesc\">The following endpoint is used to make an access token request.</p>\n                  ","href":"request_endpoint.html","attributes": {"data-id":"request_endpoint",},"menu": {"hasChildren":false,},"tocID":"request_endpoint-d66e4214","topics":[]},{"title":"Access token request","shortdesc":"\n                     <p class=\"shortdesc\">Clients make requests to token endpoints by adding the following required and         optional parameters using <code class=\"ph codeph\">application\/xwww-form-urlencoded</code> with a character         encoding of UTF-8 in the HTTP request entity-body:\n                     </p>\n                  ","href":"access_token_request.html","attributes": {"data-id":"access_token_request",},"menu": {"hasChildren":false,},"tocID":"access_token_request-d66e4225","topics":[]},{"title":"Access token response","shortdesc":"\n                     <p class=\"shortdesc\">If the access token request is valid and authorized, the authorization server issues         an access token.</p>\n                  ","href":"access_token_response.html","attributes": {"data-id":"access_token_response",},"menu": {"hasChildren":false,},"tocID":"access_token_response-d66e4239","topics":[]},{"title":"Using an access token to access the XACML evaluation service","shortdesc":"\n                     <p class=\"shortdesc\">After obtaining an access token, clients can use that token to call the PDP XACML         evaluation API.</p>\n                  ","href":"using_an_access_token_to_access_the_xacml_evaluation_service.html","attributes": {"data-id":"using_an_access_token_to_access_the_xacml_evaluation_service",},"menu": {"hasChildren":false,},"tocID":"using_an_access_token_to_access_the_xacml_evaluation_service-d66e4250","topics":[]}]});