define({"topics" : [{"title":"Using the document activity auditing level","shortdesc":"\n                  <p class=\"shortdesc\">For each profile you define, you can specify exactly which user or system activity is         recorded in the Activity Journal\n                     for all enforcers governed by that profile.\n                  </p>\n               ","href":"using_the_document_activity_auditing_level.html","attributes": {"data-id":"using_the_document_activity_auditing_level",},"menu": {"hasChildren":false,},"tocID":"using_the_document_activity_auditing_level-d66e1684","topics":[]},{"title":"Using profiles for auditing","shortdesc":"\n                  <p class=\"shortdesc\">You can define a profile purely for the purpose of gathering data for what is         referred to as a targeted audit.</p>\n               ","href":"using_profiles_for_auditing.html","attributes": {"data-id":"using_profiles_for_auditing",},"menu": {"hasChildren":false,},"tocID":"using_profiles_for_auditing-d66e1695","topics":[]}]});