define({"topics" : [{"title":"Minimum pool size","shortdesc":"\n                     <p class=\"shortdesc\">There is a minimum as well as a maximum for each connection pool, but it is not         configurable.</p>\n                  ","href":"minimum_pool_size.html","attributes": {"data-id":"minimum_pool_size",},"menu": {"hasChildren":false,},"tocID":"minimum_pool_size-d66e1800","topics":[]},{"title":"When to adjust","shortdesc":"\n                     <p class=\"shortdesc\">Whenever the operational requirements for one of the repositories begins to exceed         the number of connections allocated,\n                        you need to reallocate connections to prevent database         timeouts.\n                     </p>\n                  ","href":"when_to_adjust.html","attributes": {"data-id":"when_to_adjust",},"menu": {"hasChildren":false,},"tocID":"when_to_adjust-d66e1811","topics":[]}]});