define({"topics" : [{"title":"Choosing local or remote authentication","shortdesc":"\n                     <p class=\"shortdesc\">Control Center supports the following types of authentication.</p>\n                  ","href":"choosing_local_or_remote_authentication.html","attributes": {"data-id":"choosing_local_or_remote_authentication",},"menu": {"hasChildren":false,},"tocID":"choosing_local_or_remote_authentication-d66e2112","topics":[]},{"title":"Configuring the external domain authentication","shortdesc":"\n                     <p class=\"shortdesc\">The <code class=\"ph codeph\">&lt;ExternalDomainConfiguration&gt;</code> section contains several         properties that are used to configure information about the external domain for user and\n                        group information retrieval, and for remote authentication of users logging into Reporter         and Administrator.\n                     </p>\n                  ","href":"configuring_the_external_domain_authentication.html","attributes": {"data-id":"configuring_the_external_domain_authentication",},"menu": {"hasChildren":false,},"tocID":"configuring_the_external_domain_authentication-d66e2123","topics":[]},{"title":"Configuring trusted domains","shortdesc":"\n                     <p class=\"shortdesc\">During normal operation, Control Center tracks users and hosts according to the         domain where they are enrolled, for\n                        the purposes of preparing policy bundles. Control Center         needs this information so that it can include, in policy\n                        bundles, only the information that         is relevant to a particular policy enforcement point.\n                     </p>\n                  ","href":"configuring_trusted_domains.html","attributes": {"data-id":"configuring_trusted_domains",},"menu": {"hasChildren":false,},"tocID":"configuring_trusted_domains-d66e2137","topics":[]}]});