define({"topics" : [{"title":"Configuring data access","shortdesc":"\n               <p class=\"shortdesc\">The <span class=\"ph filepath\">configuration.xml</span> file also contains settings for connecting         to the four data stores required by Control Center.\n               </p>\n            ","href":"configuring_data_access_0.html","attributes": {"data-id":"configuring_data_access_0",},"menu": {"hasChildren":false,},"tocID":"configuring_data_access_0-d66e1750","topics":[]},{"title":"About the four databases","href":"about_the_four_databases.html","attributes": {"data-id":"about_the_four_databases",},"menu": {"hasChildren":false,},"tocID":"about_the_four_databases-d66e1764","next":"about_the_four_databases-d66e1764",},{"title":"Database management operations","shortdesc":"\n               <p class=\"shortdesc\">As the Report Server gathers data on user activity and policy enforcement, it         temporarily stores it in OLTP tables\n                  before writing it to the Activity Journal database,         where it is available for generating reports.\n               </p>\n            ","href":"database_management_operations.html","attributes": {"data-id":"database_management_operations",},"menu": {"hasChildren":false,},"tocID":"database_management_operations-d66e1844","next":"database_management_operations-d66e1844",},{"title":"Best practices","shortdesc":"\n               <p class=\"shortdesc\">The following section provides some guidance on how to use the configuration tools         described above, to maximize the\n                  performance of your Activity Journal database, and of         report generation.\n               </p>\n            ","href":"best_practices.html","attributes": {"data-id":"best_practices",},"menu": {"hasChildren":false,},"tocID":"best_practices-d66e1927","next":"best_practices-d66e1927",},{"title":"Other database tools","shortdesc":"\n               <p class=\"shortdesc\">In production environments, you should use an external database for all required data         stores. Oracle, SQL Server,\n                  and PostgreSQL are supported; the installation default is         PostgreSQL.\n               </p>\n            ","href":"other_database_tools.html","attributes": {"data-id":"other_database_tools",},"menu": {"hasChildren":false,},"tocID":"other_database_tools-d66e2004","next":"other_database_tools-d66e2004",}]});