define({"topics" : [{"title":"About deployment","shortdesc":"\n            <p class=\"shortdesc\">Deployment places objects into a state where policies can be sent to policy         enforcement points (PEPs) at the next\n               heartbeat and enforced throughout the         network.\n            </p>\n         ","href":"about_deployment.html","attributes": {"data-id":"about_deployment",},"menu": {"hasChildren":false,},"tocID":"about_deployment-d66e2762","topics":[]},{"title":"Deploying components","shortdesc":"\n            <p class=\"shortdesc\">Components used in policies must be deployed before policies that use those         components can be enforced.</p>\n         ","href":"deploying_components.html","attributes": {"data-id":"deploying_components",},"menu": {"hasChildren":false,},"tocID":"deploying_components-d66e2773","topics":[]},{"title":"Deploying policies","shortdesc":"\n            <p class=\"shortdesc\">Policies must be deployed before they can be enforced.</p>\n         ","href":"deploying_policies.html","attributes": {"data-id":"deploying_policies",},"menu": {"hasChildren":false,},"tocID":"deploying_policies-d66e2784","topics":[]},{"title":"Managing policies and components","shortdesc":"\n            <p class=\"shortdesc\"></p>\n         ","href":"managing_policies_and_components.html","attributes": {"data-id":"managing_policies_and_components",},"menu": {"hasChildren":true,},"tocID":"managing_policies_and_components-d66e2795","next":"managing_policies_and_components-d66e2795",}]});