define({"topics" : [{"title":"Setting access permissions","shortdesc":"\n                  <p class=\"shortdesc\">To enable the Control Center to connect to the SharePoint server, you need to create         a service account and set it\n                     up with proper access level and security settings.\n                  </p>\n               ","href":"setting_access_permissions.html","attributes": {"data-id":"setting_access_permissions",},"menu": {"hasChildren":false,},"tocID":"setting_access_permissions-d66e1281","topics":[]},{"title":"Enrolling SharePoint users and groups","shortdesc":"\n                  <p class=\"shortdesc\">Enrolling SharePoint groups is similar to the procedure for enrolling users and         groups.</p>\n               ","href":"enrolling_sharepoint_users_and_groups.html","attributes": {"data-id":"enrolling_sharepoint_users_and_groups",},"menu": {"hasChildren":false,},"tocID":"enrolling_sharepoint_users_and_groups-d66e1292","topics":[]},{"title":"Synchronizing SharePoint enrollments","shortdesc":"\n                  <p class=\"shortdesc\">The SharePoint connection file contains two parameters, ScheduledSyncTime and         ScheduledSyncInterval, that control\n                     the auto-synchronization feature.\n                  </p>\n               ","href":"synchronizing_sharepoint_enrollments.html","attributes": {"data-id":"synchronizing_sharepoint_enrollments",},"menu": {"hasChildren":false,},"tocID":"synchronizing_sharepoint_enrollments-d66e1303","topics":[]},{"title":"Importing SharePoint certificates","shortdesc":"\n                  <p class=\"shortdesc\">Control Center needs to connect to the monitored SharePoint servers in the network         from time to time.</p>\n               ","href":"importing_sharepoint_certificates.html","attributes": {"data-id":"importing_sharepoint_certificates",},"menu": {"hasChildren":false,},"tocID":"importing_sharepoint_certificates-d66e1314","topics":[]}]});