define({"topics" : [{"title":"General FAQs","shortdesc":"\n               <p class=\"shortdesc\"></p>\n            ","href":"general_faqs.html","attributes": {"data-id":"general_faqs",},"menu": {"hasChildren":false,},"tocID":"general_faqs-d66e5351","topics":[]},{"title":"Troubleshooting FAQs","shortdesc":"\n               <p class=\"shortdesc\"></p>\n            ","href":"troubleshooting_faqs.html","attributes": {"data-id":"troubleshooting_faqs",},"menu": {"hasChildren":false,},"tocID":"troubleshooting_faqs-d66e5361","topics":[]},{"title":"Changing memory settings","shortdesc":"\n               <p class=\"shortdesc\">Out-of-memory errors might occur during data-intensive operations, such as enrolling         large amounts of data, generating\n                  a report with more than 50,000 records, or synchronizing a         large number of activity logs.\n               </p>\n            ","href":"changing_memory_settings.html","attributes": {"data-id":"changing_memory_settings",},"menu": {"hasChildren":false,},"tocID":"changing_memory_settings-d66e5371","topics":[]},{"title":"Restarting the Control Center Policy Server","shortdesc":"\n               <p class=\"shortdesc\">Whether running on a single host, on distributed hosts, or in a failover cluster, the         Control Center Policy Server\n                  service must be manually started after the initial         installation.\n               </p>\n            ","href":"restarting_the_control_center_policy_server.html","attributes": {"data-id":"restarting_the_control_center_policy_server",},"menu": {"hasChildren":false,},"tocID":"restarting_the_control_center_policy_server-d66e5382","topics":[]},{"title":"Disabling the resource type checking in policy evaluations","shortdesc":"\n               <p class=\"shortdesc\">By default, Control Center takes resources type into account while evaluating         policies.</p>\n            ","href":"disabling_the_resource_type_checking_in_policy_evaluations.html","attributes": {"data-id":"disabling_the_resource_type_checking_in_policy_evaluations",},"menu": {"hasChildren":false,},"tocID":"disabling_the_resource_type_checking_in_policy_evaluations-d66e5393","topics":[]},{"title":"Securing the JDBC connection between Control Center and Oracle Database","shortdesc":"\n               <p class=\"shortdesc\">If the certificate is issued by a trusted CA, you do not need to follow these         steps.</p>\n            ","href":"securing_the_jdbc_connection_between_control_center_and_oracle_database.html","attributes": {"data-id":"securing_the_jdbc_connection_between_control_center_and_oracle_database",},"menu": {"hasChildren":false,},"tocID":"securing_the_jdbc_connection_between_control_center_and_oracle_database-d66e5406","topics":[]},{"title":"Extending datatypes in Oracle 12c","shortdesc":"\n               <p class=\"shortdesc\">In Oracle 12c, you can increase the maximum sizes of <code class=\"ph codeph\">VARCHAR2</code>,             <code class=\"ph codeph\">NVARCHAR2</code> and <code class=\"ph codeph\">RAW</code> data types to a maximum of 32767         bytes (not characters).\n               </p>\n            ","href":"extending_datatype_oracle12c.html","attributes": {"data-id":"generating_a_new_certificate",},"menu": {"hasChildren":false,},"tocID":"generating_a_new_certificate-d66e5417","topics":[]}]});