define({"topics" : [{"title":"Default profiles and passwords","shortdesc":"\n               <p class=\"shortdesc\">If enforcer profiles are not explicitly defined, all enforcers in the system are         automatically assigned the settings\n                  of the default profile that is automatically created in         the Administrator console.\n               </p>\n            ","href":"default_profiles_and_passwords.html","attributes": {"data-id":"default_profiles_and_passwords",},"menu": {"hasChildren":false,},"tocID":"default_profiles_and_passwords-d66e1654","topics":[]},{"title":"Defining policy enforcer profiles","href":"defining_policy_enforcer_profiles.html","attributes": {"data-id":"defining_policy_enforcer_profiles",},"menu": {"hasChildren":false,},"tocID":"defining_policy_enforcer_profiles-d66e1665","topics":[]},{"title":"About push deployment","shortdesc":"\n               <p class=\"shortdesc\">For any profile, you can enable the Push Deployment feature for all enforcers using         that profile.</p>\n            ","href":"about_push_deployment.html","attributes": {"data-id":"about_push_deployment",},"menu": {"hasChildren":false,},"tocID":"about_push_deployment-d66e1673","next":"about_push_deployment-d66e1673",},{"title":"Removing a host from a policy enforcer profile","shortdesc":"\n               <p class=\"shortdesc\">Every policy enforcer host in the network can belong to only one profile at any time.         If you decide to change the\n                  policy enforcer settings for a particular host, you do it by         simply assigning the host to a different profile.\n               </p>\n            ","href":"removing_a_host_from_a_policy_enforcer_profile.html","attributes": {"data-id":"removing_a_host_from_a_policy_enforcer_profile",},"menu": {"hasChildren":false,},"tocID":"removing_a_host_from_a_policy_enforcer_profile-d66e1706","topics":[]},{"title":"Modifying policy enforcer profiles","shortdesc":"\n               <p class=\"shortdesc\">As conditions change over time, you might want to change policy enforcer         profiles.</p>\n            ","href":"modifying_policy_enforcer_profiles.html","attributes": {"data-id":"modifying_policy_enforcer_profiles",},"menu": {"hasChildren":false,},"tocID":"modifying_policy_enforcer_profiles-d66e1717","topics":[]},{"title":"Deleting a policy enforcer profile","shortdesc":"\n               <p class=\"shortdesc\">You can delete any profile that is currently defined, which removes it from the         Control Center system.</p>\n            ","href":"deleting_a_policy_enforcer_profile.html","attributes": {"data-id":"deleting_a_policy_enforcer_profile",},"menu": {"hasChildren":false,},"tocID":"deleting_a_policy_enforcer_profile-d66e1728","topics":[]}]});