define({"topics" : [{"title":"About PEPs and interactions with PDPs","shortdesc":"\n            <p class=\"shortdesc\">PEPs (policy enforcement points) are software components that intercept user events         and enforce authorization decisions\n               provided by PDPs (policy decision points).\n            </p>\n         ","href":"about_peps_and_interactions_with_pdps.html","attributes": {"data-id":"about_peps_and_interactions_with_pdps",},"menu": {"hasChildren":true,},"tocID":"about_peps_and_interactions_with_pdps-d66e3749","next":"about_peps_and_interactions_with_pdps-d66e3749",},{"title":"Applications to be enforced and available SDKs","shortdesc":"\n            <p class=\"shortdesc\">All the SDKs provide the same core functionalityâ€”the means of creating a PEP to send         policy evaluation requests to\n               the PDP and to receive policy decisions (deny or allow) from         the PDP.\n            </p>\n         ","href":"applications_to_be_enforced_and_available_sdks.html","attributes": {"data-id":"applications_to_be_enforced_and_available_sdks",},"menu": {"hasChildren":false,},"tocID":"applications_to_be_enforced_and_available_sdks-d66e3804","topics":[]},{"title":"PEP development process","shortdesc":"\n            <p class=\"shortdesc\">Building PEPs involves these steps.</p>\n         ","href":"pep_development_process.html","attributes": {"data-id":"pep_development_process",},"menu": {"hasChildren":false,},"tocID":"pep_development_process-d66e3815","topics":[]},{"title":"Policy Controller components","shortdesc":"\n            <p class=\"shortdesc\">The functional components of the Policy Controller (PDP) include the         following.</p>\n         ","href":"policy_controller_components.html","attributes": {"data-id":"policy_controller_components",},"menu": {"hasChildren":false,},"tocID":"policy_controller_components-d66e3826","topics":[]}]});