define({"topics" : [{"title":"About policies","shortdesc":"\n            <p class=\"shortdesc\">Policies are statements that describe resource usage situations and specify the         actions to be taken when those situations\n               arise.\n            </p>\n         ","href":"about_policies.html","attributes": {"data-id":"about_policies",},"menu": {"hasChildren":false,},"tocID":"about_policies-d66e2370","topics":[]},{"title":"Overview of policy implementation","shortdesc":"\n            <p class=\"shortdesc\">Implementing policies involves several tasks.</p>\n         ","href":"overview_of_policy_implementation.html","attributes": {"data-id":"overview_of_policy_implementation",},"menu": {"hasChildren":false,},"tocID":"overview_of_policy_implementation-d66e2381","topics":[]},{"title":"Managing Policy Model resource types","shortdesc":"\n            <p class=\"shortdesc\">Policy Model resource types are the templates that include information about         attributes, actions, and obligations.</p>\n         ","href":"managing_policy_model_resource_types.html","attributes": {"data-id":"managing_policy_model_resource_types",},"menu": {"hasChildren":true,},"tocID":"managing_policy_model_resource_types-d66e2392","next":"managing_policy_model_resource_types-d66e2392",},{"title":"Managing policy components","shortdesc":"\n            <p class=\"shortdesc\">Policy components are the abstract building blocks or raw material of information         control policies.</p>\n         ","href":"managing_policy_components.html","attributes": {"data-id":"managing_policy_components",},"menu": {"hasChildren":true,},"tocID":"managing_policy_components-d66e2425","next":"managing_policy_components-d66e2425",}]});