define({"topics" : [{"title":"CEM_AllocateString()","shortdesc":"\n                     <p class=\"shortdesc\">This call provides a basic service to allocate memory for mapping a CEtype string to         a C or C++ string, so that it\n                        can be used by the system. \n                     </p>\n                  ","href":"cem_allocatestring.html","attributes": {"data-id":"template",},"menu": {"hasChildren":false,},"tocID":"template-d66e5099","topics":[]},{"title":"CEM_FreeString()","shortdesc":"\n                     <p class=\"shortdesc\">This call frees a CE-type string from the system.</p>\n                  ","href":"cem_freestring.html","attributes": {"data-id":"template",},"menu": {"hasChildren":false,},"tocID":"template-d66e5110","topics":[]},{"title":"CEM_ReallocateString()","shortdesc":"\n                     <p class=\"shortdesc\">This call reallocates the existing string to a new string.</p>\n                  ","href":"cem_reallocatestring.html","attributes": {"data-id":"template",},"menu": {"hasChildren":false,},"tocID":"template-d66e5121","topics":[]},{"title":"CEM_GetString()","shortdesc":"\n                     <p class=\"shortdesc\">Basic service to retrieve the standard C string from the CE-type string.</p>\n                  ","href":"cem_getstring.html","attributes": {"data-id":"template",},"menu": {"hasChildren":false,},"tocID":"template-d66e5132","topics":[]}]});