define({"topics" : [{"title":"Manual synchronization","shortdesc":"\n                  <p class=\"shortdesc\">The <span class=\"keyword cmdname\">-sync</span> command reuses the parameters provided the last time the         enroll command was called for the specified enrollments—including\n                     the references to the         appropriate definition and connection files—based on the enrollment name parameter         \n                     (<span class=\"keyword cmdname\">-n</span>).\n                  </p>\n               ","href":"manual_synchronization.html","attributes": {"data-id":"manual_synchronization",},"menu": {"hasChildren":false,},"tocID":"manual_synchronization-d66e1011","topics":[]},{"title":"Incremental synchronization using LDIF delta file","shortdesc":"\n                  <p class=\"shortdesc\">If you want make changes to an existing enrollment after performing manual         synchronization, you can use the Enrollment\n                     Manager utility with the             <span class=\"keyword cmdname\">-delta</span> option to perform incremental synchronization.\n                  </p>\n               ","href":"incremental_synchronization_using_ldif_delta_file.html","attributes": {"data-id":"incremental_synchronization_using_ldif_delta_file",},"menu": {"hasChildren":false,},"tocID":"incremental_synchronization_using_ldif_delta_file-d66e1028","topics":[]},{"title":"Scheduled synchronization","shortdesc":"\n                  <p class=\"shortdesc\">You can configure an enrollment to be automatically synchronized on a regular         schedule. This is recommended for all\n                     production implementations.\n                  </p>\n               ","href":"scheduled_synchronization.html","attributes": {"data-id":"scheduled_synchronization",},"menu": {"hasChildren":false,},"tocID":"scheduled_synchronization-d66e1042","topics":[]},{"title":"Scheduled synchronizations for Domain Group enrollments","shortdesc":"\n                  <p class=\"shortdesc\">Enrollment Manager synchronizes domains one at a time. When you schedule         synchronization intervals for a Domain Group,\n                     you should allow enough time for all         subdomains to be processed.\n                  </p>\n               ","href":"scheduled_synchronizations_for_domain_group_enrollments.html","attributes": {"data-id":"scheduled_synchronizations_for_domain_group_enrollments",},"menu": {"hasChildren":false,},"tocID":"scheduled_synchronizations_for_domain_group_enrollments-d66e1053","topics":[]}]});