define({"topics" : [{"title":"Creating audit policies","shortdesc":"\n               <p class=\"shortdesc\"></p>\n            ","href":"creating_audit_policies.html","attributes": {"data-id":"creating_audit_policies",},"menu": {"hasChildren":false,},"tocID":"creating_audit_policies-d66e2684","topics":[]},{"title":"Allow policy obligations","shortdesc":"\n               <p class=\"shortdesc\">When you define a new policy with Allow, you can only associate On Allow, Monitor         obligations with it.</p>\n            ","href":"allow_policy_obligations.html","attributes": {"data-id":"allow_policy_obligations",},"menu": {"hasChildren":false,},"tocID":"allow_policy_obligations-d66e2694","topics":[]},{"title":"About enforcement logging","shortdesc":"\n               <p class=\"shortdesc\">Enforcement logging can be configured as an obligation, associated with each         individual policy you construct.</p>\n            ","href":"about_enforcement_logging.html","attributes": {"data-id":"about_enforcement_logging",},"menu": {"hasChildren":false,},"tocID":"about_enforcement_logging-d66e2705","topics":[]},{"title":"Logging vs. email","shortdesc":"\n               <p class=\"shortdesc\">To reduce email traffic and clutter in administrator mailboxes, avoid enabling email         notifications for policies unless\n                  there is a real need to do so.\n               </p>\n            ","href":"logging_vs._email.html","attributes": {"data-id":"logging_vs._email",},"menu": {"hasChildren":false,},"tocID":"logging_vs._email-d66e2716","topics":[]}]});