define({"topics" : [{"title":"Setting up the JavaScript SDK","shortdesc":"\n               <p class=\"shortdesc\">To use the JavaScript client library, set up the JavaScript SDK.</p>\n            ","href":"setting_up_the_javascript_sdk.html","attributes": {"data-id":"setting_up_the_javascript_sdk",},"menu": {"hasChildren":false,},"tocID":"setting_up_the_javascript_sdk-d66e4073","topics":[]},{"title":"Required properties","shortdesc":"\n               <p class=\"shortdesc\">Properties can be loaded through <span class=\"ph filepath\">openAz-pep.json</span> or as a         JavaScript object. The following properties are required to connect to the REST         API.\n               </p>\n            ","href":"required_properties.html","attributes": {"data-id":"required_properties",},"menu": {"hasChildren":false,},"tocID":"required_properties-d66e4084","topics":[]},{"title":"Making authorization requests using JavaScript","shortdesc":"\n               <p class=\"shortdesc\">PEPs (policy enforcement points) send authorization requests to PDPs (policy decision         points) and receive authorization\n                  decisions in response.\n               </p>\n            ","href":"making_authorization_requests_using_javascript.html","attributes": {"data-id":"making_authorization_requests_using_javascript",},"menu": {"hasChildren":false,},"tocID":"making_authorization_requests_using_javascript-d66e4098","topics":[]},{"title":"Configuring user authentication for the REST API","shortdesc":"\n               <p class=\"shortdesc\">The CloudAz REST API uses OAuth 2.0 for authentication. To help you get started         quickly, the CloudAz instance comes\n                  preconfigured with <code class=\"ph codeph\">client_id</code> and             <code class=\"ph codeph\">client_secret</code> credentials.\n               </p>\n            ","href":"configuring_user_authentication_for_the_rest_api.html","attributes": {"data-id":"configuring_user_authentication_for_the_rest_api",},"menu": {"hasChildren":false,},"tocID":"configuring_user_authentication_for_the_rest_api-d66e4109","topics":[]}]});