define({"topics" : [{"title":"Monitoring functionality and permissions","shortdesc":"\n               <p class=\"shortdesc\">In Reporter, you use the Monitoring tab to create policy monitors and to view alerts         triggered by those monitors.</p>\n            ","href":"monitoring_functionality_and_permissions.html","attributes": {"data-id":"monitoring_functionality_and_permissions",},"menu": {"hasChildren":false,},"tocID":"monitoring_functionality_and_permissions-d66e3427","topics":[]},{"title":"Creating a monitor","shortdesc":"\n               <p class=\"shortdesc\">Creating a monitor is similar to creating a report. Both access policy activity data         from the Activity Journal. </p>\n            ","href":"creating_a_monitor.html","attributes": {"data-id":"creating_a_monitor",},"menu": {"hasChildren":false,},"tocID":"creating_a_monitor-d66e3438","next":"creating_a_monitor-d66e3438",},{"title":"Deactivating and deleting monitors","shortdesc":"\n               <p class=\"shortdesc\">When you create and save a monitor, it is activated automatically, and stays in         effect until you deactivate or delete\n                  it.\n               </p>\n            ","href":"deactivating_and_deleting_monitors.html","attributes": {"data-id":"deactivating_and_deleting_monitors",},"menu": {"hasChildren":false,},"tocID":"deactivating_and_deleting_monitors-d66e3484","topics":[]},{"title":"Viewing alerts","shortdesc":"\n               <p class=\"shortdesc\">The Alerts Overview tab provides a summary list of alerts in a table. </p>\n            ","href":"viewing_alerts.html","attributes": {"data-id":"viewing_alerts",},"menu": {"hasChildren":false,},"tocID":"viewing_alerts-d66e3495","next":"viewing_alerts-d66e3495",},{"title":"Analyzing alerts","shortdesc":"\n               <p class=\"shortdesc\">In addition to displaying the complete list of alerts, Reporter provides the option         of viewing and analyzing alerts.</p>\n            ","href":"analyzing_alerts.html","attributes": {"data-id":"analyzing_alerts",},"menu": {"hasChildren":false,},"tocID":"analyzing_alerts-d66e3594","next":"analyzing_alerts-d66e3594",},{"title":"Using Audit Logs","shortdesc":"\n               <p class=\"shortdesc\">This section describes how to use the Audit Logs tab to view activity         logs.</p>\n            ","href":"using_audit_logs.html","attributes": {"data-id":"using_audit_logs",},"menu": {"hasChildren":false,},"tocID":"using_audit_logs-d66e3638","next":"using_audit_logs-d66e3638",},{"title":"Sample reports","shortdesc":"\n               <p class=\"shortdesc\">This section presents examples of different report formats, all representing a small         set of event data returned by\n                  the same report query.\n               </p>\n            ","href":"sample_reports.html","attributes": {"data-id":"sample_reports",},"menu": {"hasChildren":false,},"tocID":"sample_reports-d66e3671","next":"sample_reports-d66e3671",}]});